# hash code and binary search
def findLength(nums1:list, nums2:list):

    # determine if sub array exist
    def findifSubarrayExist(k):
        seen = {}
        base = 113
        MOD = 10**9 + 7
        for i in range( len(nums1) - k + 1):
            hashint = 0
            for j in range(k):
                hashint = hashint * base +nums1[i+j]
            if hashint // MOD != 0:
                seen.setdefault(hashint % MOD , [])
                seen[hashint % MOD].append(hashint // MOD)
            else:
                seen.setdefault(hashint % MOD , 0)
                seen[hashint] +=1

        for i in range(len(nums2) - k + 1):
            hashint = 0
            for j in range(k):
                hashint = (hashint * base +nums2[i+j])
            if hashint % MOD in seen.keys():
                if type(seen[hashint % MOD]) == int:
                    return True
                else:
                    if hashint // MOD in seen[hashint % MOD]:
                        return True

        return False

    # binary search
    low, high = 1, min( len(nums1) , len(nums2)) + 1
    while low < high:
        mi = (low + high) // 2
        if findifSubarrayExist(mi):
            low = mi + 1
        else:
            high = mi

    return low - 1

